mapper.select:
SELECT xm.xiangmuid,xm.xiangmuname,jg.jungongjiesuanid,jg.jiesuanzhuangtaiid,jszt.text as jszttext, date_format(jg.jiesuanwanchengtime,'%Y-%m') as jiesuanwanchengtime, jg.jiesuanqingkuangid,jsqk.text as jsqktext,jg.shifoujizhang,jg.jiesuanpifuwenhao, jg.jiesuanpifujine,jg.jieyushangjiaojine from junjianxiangmu xm left join jungongjiesuan jg on jg.xiangmuid=jg.xiangmuid left join dict_jiesuanzhuangtai jszt on jszt.id=jg.jiesuanzhuangtaiid left join dict_jiesuanqingkuang jsqk on jsqk.id=jg.jiesuanqingkuangid where 1=1
and xiangmuname like #{xiangmuname}
and xm.jieshoudanweiid=#{deptid}
order by xm.xiangmuname LIMIT #{startindex},#{pagesize}

SELECT count(*) from junjianxiangmu xm left join jungongjiesuan jg on jg.xiangmuid=jg.xiangmuid left join dict_jiesuanzhuangtai jszt on jszt.id=jg.jiesuanzhuangtaiid left join dict_jiesuanqingkuang jsqk on jsqk.id=jg.jiesuanqingkuangid where 1=1
and xiangmuname like #{xiangmuname}
and xm.jieshoudanweiid=#{deptid}
